package yaktest

import "testing"

func TestPocRunner(t *testing.T) {
	cases := []YakTestCase{
		{Name: "测试超长 URI", Src: `
rsp, req, err := poc.HTTP(` + "`" + `GET /?unix:|http://example.com HTTP/1.1
Host: 127.0.0.1:8080
User-Agent: Mozilla/5.0 (Windows NT 6.1; Win64; x64; rv:47.0) Gecko/20100101 Firefox/47.0

` + "`" + `, poc.noRedirect(true))
die(err)

dump(rsp)

`},
	}
	Run("poc.HTTP long uri", t, cases...)
}

func TestPoC(t *testing.T) {
	cases := []YakTestCase{
		{Name: "PoC", Src: `
dump(fuzz.Strings(abc[0]))
`},
	}
	Run("fuzz.Unquote", t, cases...)
}
