package yserx

import (
	"bufio"
	"bytes"
	"os"
	"yaklang/common/yak/yaklib/codec"
	"testing"
)

func TestParseJavaSerializedEx(t *testing.T) {
	origin := ""
	raw, _ := codec.DecodeHex(origin)
	objs, err := ParseJavaSerializedEx(bufio.NewReader(bytes.NewBuffer(raw)), os.Stdout)
	if err != nil {
		panic(err)
	}
	_ = objs
	if objs == nil {
		panic("err! for parsing")
	}

	if codec.EncodeToHex(MarshalJavaObjects(objs...)) != origin {
		panic(1)
	}
}
